-- 表的结构 `xy_duty`
--

CREATE TABLE IF NOT EXISTS `xy_duty` (
`id` smallint(3) NOT NULL,
  `duty_no` varchar(10) NOT NULL DEFAULT '' COMMENT '职责编号',
  `name` varchar(50) NOT NULL DEFAULT '' COMMENT '名称',
  `sort` varchar(20) NOT NULL DEFAULT '' COMMENT '排序',
